CREATE TABLE "public"."delivery" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "title" varchar, "description" text, "pickup_location" point NOT NULL, "delivery_location" point NOT NULL, "sender" text NOT NULL, "receiver" Text NOT NULL, "status" varchar NOT NULL DEFAULT 'requested', "size" varchar NOT NULL, "courier_id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("status") REFERENCES "public"."delivery_status"("status_name") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("size") REFERENCES "public"."shipment_size"("name") ON UPDATE no action ON DELETE no action, UNIQUE ("id"));COMMENT ON TABLE "public"."delivery" IS E'it include all life cycle of delivery form the request to delivery compilation.';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
